function getAverage(numbers) {
    if (numbers.length === 0) {
        return 0;
    }

    const sum = numbers.reduce((a, b) => a + b, 0);
    return sum / numbers.length;
}

function getAverageOfOddNums(numbers) {
    if (numbers.length === 0) {
        return 0;
    }

    const oddNums = numbers.filter(n => n % 2 !== 0);
    if (oddNums.length === 0) {
        return 0;
    }

    const sum = oddNums.reduce((a, b) => a + b, 0);
    return sum / oddNums.length;
}
